import React, { useState } from 'react';
import { motion } from 'framer-motion';
import * as ReactDOMClient from 'react-dom/client';

// Define box dimensions and spacing
const BOX_WIDTH = 200;
const BOX_HEIGHT = 200;
const INITIAL_CIRCLES = 9; // Initial number of circles (3 rows * 3 columns)

function generateRandomPositionInBox() {
    const top = Math.floor(Math.random() * (BOX_HEIGHT - 100)); // Adjust for button height
    const left = Math.floor(Math.random() * (BOX_WIDTH - 100)); // Adjust for button width
    return { top, left };
}

function generateRandomScale() {
    return 1 + Math.random() * 0.5; // Random scale between 1 and 1.5
}

function PulsatingButton({ text }) {
    const [position] = useState(generateRandomPositionInBox);
    const [scale] = useState(generateRandomScale); // Random scale factor

    return (
        <motion.button
            style={{
                position: 'absolute',
                top: position.top,
                left: position.left,
                width: 100,
                height: 100,
                borderRadius: '50%',
                backgroundColor: '#1A06D0',
                color: 'white',
                fontSize: '16px',
                fontWeight: 'bold',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                border: 'none',
                cursor: 'pointer',
                outline: 'none',
            }}
            animate={{
                scale: [1, scale, 1], // Apply random scale factor in animation
            }}
            transition={{
                duration: 5,
                ease: 'easeInOut',
                repeat: Infinity,
            }}
            whileHover={{ scale: scale + 0.1 }} // Slightly increase scale on hover
            onClick={() => alert(`${text} clicked!`)}
        >
            {text}
        </motion.button>
    );
}

const Hero = () => {
    const [circles, setCircles] = useState(INITIAL_CIRCLES); // Initially 9 circles (3 rows * 3 columns)

    // Function to add one more circle to the page
    const addCircle = () => {
        setCircles(prevCircles => prevCircles + 1); // Add one more circle each time
    };

    return (
        <section>
            {/* Body with Circles */}
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 20 }}>
                {/* Create circles based on the `circles` state */}
                {Array.from({ length: circles }).map((_, index) => (
                    <div key={index} style={{ position: 'relative', display: 'flex', gap: 20 }}>
                        <div
                            key={index}
                            style={{
                                position: 'relative',
                                width: BOX_WIDTH,
                                height: BOX_HEIGHT,
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                            }}
                        >
                            <PulsatingButton text={`Circle ${index + 1}`} />
                        </div>
                    </div>
                ))}
            </div>

            {/* Add Circle Button */}
            <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                <button
                    onClick={addCircle}
                    style={{
                        padding: '10px 20px',
                        fontSize: '16px',
                        backgroundColor: '#3498db',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                    }}
                >
                    Add Circle
                </button>
            </div>
        </section>
    );
}

const Home = () => {
    return <Hero />;
}

export default Home;
