import React, { useState } from 'react';
import { motion } from 'framer-motion';
import * as ReactDOMClient from 'react-dom/client';

// Define box dimensions and spacing
const BOX_WIDTH = 200;
const BOX_HEIGHT = 200;
const COLUMNS = 3; // 3 circles per row

function generateRandomPositionInBox() {
  const top = Math.floor(Math.random() * (BOX_HEIGHT - 100)); // Adjust for button height
  const left = Math.floor(Math.random() * (BOX_WIDTH - 100)); // Adjust for button width
  return { top, left };
}

function generateRandomScale() {
  return 1 + Math.random() * 0.5; // Random scale between 1 and 1.5
}

function PulsatingButton({ text, name }) {
  const [position] = useState(generateRandomPositionInBox);
  const [scale] = useState(generateRandomScale); // Random scale factor

  return (
    <motion.button
      style={{
        position: 'absolute',
        top: position.top,
        left: position.left,
        width: 100,
        height: 100,
        borderRadius: '50%',
        backgroundColor: '#1A06D0',
        color: 'white',
        fontSize: '16px',
        fontWeight: 'bold',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        border: 'none',
        cursor: 'pointer',
        outline: 'none',
      }}
      animate={{
        scale: [1, scale, 1], // Apply random scale factor in animation
      }}
      transition={{
        duration: 5,
        ease: 'easeInOut',
        repeat: Infinity,
      }}
      whileHover={{ scale: scale + 0.1 }} // Slightly increase scale on hover
      onClick={() => alert(`${name} clicked!`)}
    >
      {text}
    </motion.button>
  );
}

const Hero = () => {
  const [circles, setCircles] = useState([]); // Array of circles
  const [newCircleName, setNewCircleName] = useState(''); // State to store new circle name

  // Function to add one more circle to the page
  const addCircle = () => {
    // Prompt user for a name when adding a circle
    const circleName = prompt('Name your group', 'Circle ' + (circles.length + 1)); // Default name is Circle X
    if (circleName) {
      setCircles(prevCircles => [
        ...prevCircles,
        { id: prevCircles.length + 1, name: circleName },
      ]);
    }
  };

  // Calculate the number of rows based on the total circles
  const rows = Math.ceil(circles.length / COLUMNS);

  return (
    <section>
      {/* Body with Circles */}
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 20 }}>
        {/* Create rows dynamically */}
        {Array.from({ length: rows }).map((_, rowIndex) => (
          <div key={rowIndex} style={{ display: 'flex', gap: 20 }}>
            {/* Create 3 circles per row */}
            {Array.from({ length: COLUMNS }).map((_, colIndex) => {
              const circleIndex = rowIndex * COLUMNS + colIndex; // Calculate the circle index
              if (circleIndex < circles.length) {
                const circle = circles[circleIndex];
                return (
                  <div
                    key={circle.id}
                    style={{
                      position: 'relative',
                      width: BOX_WIDTH,
                      height: BOX_HEIGHT,
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <PulsatingButton text={circle.name} name={circle.name} />
                  </div>
                );
              }
              return null; // Don't render empty spots if no circle is there
            })}
          </div>
        ))}
      </div>

      {/* Add Circle Button */}
      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <button
          onClick={addCircle}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            backgroundColor: '#3498db',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Add Circle
        </button>
      </div>
    </section>
  );
};

const Home = () => {
  return <Hero />;
};

export default Home;
