import React, { useState } from 'react';
import { motion } from 'framer-motion';

// Define default values
const BOX_WIDTH = 200;
const BOX_HEIGHT = 200;

// Random position generation for each circle within the bounding box
function generateRandomPositionInBox(circles, size) {
  let top, left;
  const attempts = 100; // Limit the number of attempts to avoid infinite loop
  let validPosition = false;

  for (let attempt = 0; attempt < attempts; attempt++) {
    top = Math.floor(Math.random() * (BOX_HEIGHT - size));
    left = Math.floor(Math.random() * (BOX_WIDTH - size));

    // Check if the new circle position intersects with any existing circles
    validPosition = !circles.some(circle => {
      const distX = Math.abs(circle.left - left);
      const distY = Math.abs(circle.top - top);
      const combinedRadius = (circle.size + size) / 2;
      return distX < combinedRadius && distY < combinedRadius;
    });

    if (validPosition) break; // Found a valid position
  }

  return { top, left };
}

// Random scale generation for each circle (between 1 and 1.2 for subtle size variations)
function generateRandomScale() {
  return 1 + Math.random() * 0.5; // Random scale between 1 and 1.5
}

// Random wiggle values for animation
function generateRandomWiggle() {
  return Math.random() * 10 - 5; // Random wiggle value between -5 and 5
}

// Random size for each circle (between 60px and 160px for more spread)
function generateRandomSize() {
  return Math.floor(Math.random() * (160 - 60 + 1)) + 60; // Random size between 60px and 160px
}

// Updated random animation duration (between 6 and 10 seconds)
function generateRandomDuration() {
  return 6 + Math.random() * 4; // Random duration between 6 and 10 seconds
}

function PulsatingButton({ text, name, size, position, isHovered }) {
    const [scale] = useState(generateRandomScale); // Random scale factor
    const [duration] = useState(generateRandomDuration); // Random animation duration
    const [currentSize, setCurrentSize] = useState(size); // Track current size to manage hover state

    useEffect(() => {
      if (isHovered) {
        setCurrentSize(size + 20); // Increase size by 20px on hover
      } else {
        setCurrentSize(size); // Return to original size when hover ends
      }
    }, [isHovered, size]);

    return (
      <motion.button
        style={{
          position: 'absolute',
          top: position.top,
          left: position.left,
          width: currentSize, // Use the dynamic size
          height: currentSize, // Use the dynamic size
          borderRadius: '50%',
          backgroundColor: '#1A06D0', // Default background color
          color: 'white',
          fontSize: '16px',
          fontWeight: 'bold',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          border: 'none',
          cursor: 'pointer',
          outline: 'none',
        }}
        animate={{
          scale: [1, scale, 1], // Apply random scale factor in animation (subtle)
          x: [0, generateRandomWiggle(), generateRandomWiggle(), generateRandomWiggle(), 0], // Random X wiggles
          y: [0, generateRandomWiggle(), generateRandomWiggle(), generateRandomWiggle(), 0], // Random Y wiggles
        }}
        transition={{
          duration: duration, // Apply random duration for animation
          ease: 'easeInOut',
          repeat: Infinity,
          repeatType: 'loop',
        }}
        whileHover={{
          scale: scale + 0.15, // Increase scale on hover
          transition: { 
            scale: { 
              duration: 0.6, // Slower transition for increase in size
              ease: 'easeInOut'
            },
          },
        }}
        whileTap={{
          scale: scale + 0.1, // Slightly smaller scale when tapped (optional)
          transition: {
            scale: { duration: 0.3, ease: 'easeOut' }, // Smoother scale back after tap
          },
        }}
        onMouseEnter={() => setIsHovered(true)}  // Set hover state when mouse enters
        onMouseLeave={() => setIsHovered(false)} // Reset hover state when mouse leaves
      >
        {text}
      </motion.button>
    );
  }




  const Hero = () => {
    const [circles, setCircles] = useState([]); // Array of circles
    const [screenWidth, setScreenWidth] = useState(window.innerWidth); // Store screen width

    const [hoveredCircle, setHoveredCircle] = useState(null); // Track which circle is hovered

    // Function to update the screen width on resize
    useEffect(() => {
      const handleResize = () => {
        setScreenWidth(window.innerWidth);
      };
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }, []);

    // Randomize the number of circles per row between 3 and 4
    const getCirclesPerRow = () => {
      return Math.floor(Math.random() * (4 - 3 + 1)) + 3; // Random number between 3 and 4
    };

    // Function to add one more circle to the page
    const addCircle = () => {
      const circleName = prompt('Name your group', 'Circle ' + (circles.length + 1)); // Default name is Circle X
      if (circleName) {
        const randomSize = generateRandomSize(); // Get a random size for this circle
        setCircles(prevCircles => [
          ...prevCircles,
          { id: prevCircles.length + 1, name: circleName, size: randomSize, isHovered: false },
        ]);
      }
    };

    return (
      <section style={{ width: '100%', padding: 0, margin: 0 }}>
        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            gap: 20,
            width: '100%',
          }}
        >
          {/* Create dynamic number of circles */}
          {circles.map((circle, index) => {
            const colSize = getCirclesPerRow(); // Random number of circles per row
            const position = generateRandomPositionInBox();
            return (
              <div
                key={circle.id}
                style={{
                  position: 'relative',
                  width: BOX_WIDTH,
                  height: BOX_HEIGHT,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  margin: 10,
                }}
              >
                <PulsatingButton
                  text={circle.name}
                  name={circle.name}
                  size={circle.size}
                  position={position}
                  isHovered={hoveredCircle === circle.id}
                />
              </div>
            );
          })}
        </div>

        {/* Add Circle Button */}
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          <button
            onClick={addCircle}
            style={{
              padding: '10px 20px',
              fontSize: '16px',
              backgroundColor: '#3498db',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
            }}
          >
            Add Circle
          </button>
        </div>
      </section>
    );
  };


const Home = () => {
  return <Hero />;
};

export default Home;
