import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import Header from '../../ui/header/header';
import Footer from '../../ui/footer/footer';

const BOX_WIDTH = 200;
const BOX_HEIGHT = 200;

function generateRandomPositionInBox(circles, size) {
  let top, left;
  const attempts = 100;
  let validPosition = false;

  for (let attempt = 0; attempt < attempts; attempt++) {
    top = Math.floor(Math.random() * (BOX_HEIGHT - size));
    left = Math.floor(Math.random() * (BOX_WIDTH - size));

    validPosition = !circles.some(circle => {
      const distX = Math.abs(circle.left - left);
      const distY = Math.abs(circle.top - top);
      const combinedRadius = (circle.size + size) / 2;
      return distX < combinedRadius && distY < combinedRadius;
    });

    if (validPosition) break;
  }

  return { top, left };
}

function generateRandomSize() {
  return Math.floor(Math.random() * (160 - 60 + 1)) + 60;
}

function generateRandomDuration() {
  return 6 + Math.random() * 4;
}

function PulsatingButton({ text, name, size, position }) {
  const [isHovered, setIsHovered] = useState(false);

  const wiggleX = Array(5).fill().map(() => Math.random() * 10 - 5);
  const wiggleY = Array(5).fill().map(() => Math.random() * 10 - 5);

  return (
    <motion.button
      style={{
        position: 'absolute',
        top: position.top,
        left: position.left,
        width: size,
        height: size,
        borderRadius: '50%',
        color: 'white',
        fontSize: '16px',
        fontWeight: 'bold',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        border: 'none',
        cursor: 'pointer',
        outline: 'none',
        backgroundColor: isHovered ? '#152474' : '#1E3A8A',
      }}
      animate={{
        scale: isHovered ? 1.15 : 1,
        x: isHovered ? 0 : wiggleX,
        y: isHovered ? 0 : wiggleY,
      }}
      transition={{
        scale: { duration: 0.4, ease: 'easeInOut' },
        x: {
          duration: generateRandomDuration(),
          ease: 'easeInOut',
          repeat: Infinity,
          repeatType: 'mirror',
          delay: isHovered ? 0 : 0.2, // Small delay to transition back to wiggle
        },
        y: {
          duration: generateRandomDuration(),
          ease: 'easeInOut',
          repeat: Infinity,
          repeatType: 'mirror',
          delay: isHovered ? 0 : 0.2,
        },
        transitionEnd: {
          x: {
            duration: 0.5,
            ease: 'easeInOut',
          },
          y: {
            duration: 0.5,
            ease: 'easeInOut',
          },
        },
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={() => alert(`${name} clicked!`)}
    >
      {text}
    </motion.button>
  );
}

const Hero = () => {
  const [circles, setCircles] = useState([]);

  const getCirclesPerRow = () => Math.floor(Math.random() * (4 - 3 + 1)) + 3;

  const addCircle = () => {
    const circleName = prompt('Name your group', 'Circle ' + (circles.length + 1));
    if (circleName) {
      const randomSize = generateRandomSize();
      const position = generateRandomPositionInBox(circles, randomSize);
      setCircles(prevCircles => [
        ...prevCircles,
        { id: prevCircles.length + 1, name: circleName, size: randomSize, ...position },
      ]);
    }
  };

  const getRows = async () => {
    let rows = [];
    try {
      const token = localStorage.getItem('authToken');

      const response = await fetch('http://10.84.6.232:5000/api/home', {
        method: 'GET',
        headers: {
          "Authorization": "Bearer " + token,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        rows = await response.json();

        rows = rows?.names

        console.log(rows)
      } else {
        return [];
      }
    } catch (err) {
      return [];
    }

    let circlesCopy = [...circles];
    while (circlesCopy.length > 0) {
      const circlesInRow = getCirclesPerRow();
      const rowCircles = circlesCopy.splice(0, circlesInRow);
      rows.push(rowCircles);
    }
    // console.log(rows)
    return rows;
  };

  const rows = getRows().then(response => response.json());

  return (
    <section style={{ width: '100%', padding: 0, margin: 0 }}>
      {/* Centering the search bar */}
      <div style={{ display: 'flex', justifyContent: 'center', width: '100%', marginBottom: '20px' }}>
        <form className="w-2/5">
          <label htmlFor="default-search" className="mb-2 text-sm font-medium text-slate-50 sr-only">Search</label>
          <div className="relative">
            <div className="absolute inset-y-0 start-0 flex items-center ps-3 pointer-events-none">
              <svg className="w-4 h-4 text-slate-50" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20">
                <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="m19 19-4-4m0-7A7 7 0 1 1 1 8a7 7 0 0 1 14 0Z" />
              </svg>
            </div>
            <input
              type="search"
              id="default-search"
              className="block w-full px-4 py-3 ps-10 text-sm text-slate-50 rounded-lg bg-blue-950 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Search circles"
            />
          </div>
        </form>
      </div>

      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '100%',
        }}
      >
        {rows.map((row, rowIndex) => (
          <motion.div
            key={rowIndex}
            style={{
              display: 'flex',
              justifyContent: 'center',
              gap: 20,
              width: '100%',
            }}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: 'easeOut', delay: rowIndex * 0.2 }}
          >
            {row.map((circle) => (
              <div
                key={circle.id}
                style={{
                  position: 'relative',
                  width: BOX_WIDTH,
                  height: BOX_HEIGHT,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                }}
              >
                <PulsatingButton text={circle.name} name={circle.name} size={circle.size} position={{ top: circle.top, left: circle.left }} />
              </div>
            ))}
          </motion.div>
        ))}
      </div>

      <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
        <motion.button
          onClick={addCircle}
          style={{
            padding: '10px 20px',
            fontSize: '16px',
            backgroundColor: '#152474',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Add Circle
        </motion.button>
      </div>
    </section>
  );
};

const Home = () => {
  return (
    <>
      <Header />
      <Hero />
      <Footer />
    </>
  );
};

export default Home;
