import React, { useState } from "react";
import { useParams } from "react-router-dom";

// ChatBubble component for individual message display
const ChatBubble = ({ text, sender }) => {
  return (
    <div
      style={{
        maxWidth: "80%",
        backgroundColor: sender === "me" ? "#3498db" : "#e1e1e1",
        color: sender === "me" ? "white" : "black",
        padding: "10px 20px",
        borderRadius: "20px",
        margin: "10px 0",
        alignSelf: sender === "me" ? "flex-end" : "flex-start",
        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {text}
    </div>
  );
};

// Messenger component to manage and display chat messages
const Messenger = () => {
  const { circleId } = useParams(); // Get circleId from URL
  const [messages, setMessages] = useState(messagesData[circleId] || []);
  const [messageInput, setMessageInput] = useState("");

  // Send message handler
  const sendMessage = () => {
    if (messageInput.trim()) {
      setMessages([...messages, { sender: "me", text: messageInput }]); // Append new message at the end
      setMessageInput(""); // Clear the input field after sending
    }
  };

  // Handle the 'Enter' key press to send the message
  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      sendMessage();
    }
  };

  return (
    <div style={{ display: "flex", height: "100vh", flexDirection: "column" }}>
      {/* Header */}
      <div
        style={{
          backgroundColor: "#1E3A8A",
          padding: "15px",
          color: "white",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
        }}
      >
        <div style={{ display: "flex", alignItems: "center" }}>
          <div
            style={{
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              backgroundColor: "white",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              marginRight: "10px",
            }}
          >
            <span style={{ fontSize: "20px", color: "#1E3A8A" }}>{circleId}</span>
          </div>
          <span style={{ fontSize: "18px" }}>{circleId}</span>
        </div>
        <button
          style={{
            backgroundColor: "#fff",
            color: "#1E3A8A",
            border: "none",
            padding: "8px 16px",
            borderRadius: "5px",
            cursor: "pointer",
            fontSize: "14px",
          }}
        >
          Settings
        </button>
      </div>

      {/* Messages Area */}
      <div
        style={{
          flex: 1,
          padding: "20px",
          backgroundColor: "#f0f0f0",
          overflowY: "scroll",
          display: "flex",
          flexDirection: "column", // Standard column direction
        }}
      >
        {messages.map((message, index) => (
          <ChatBubble key={index} text={message.text} sender={message.sender} />
        ))}
      </div>

      {/* Input Area with Form */}
      <div
        style={{
          display: "flex",
          alignItems: "center",
          padding: "10px",
          backgroundColor: "#fff",
          borderTop: "1px solid #ddd",
        }}
      >
        <input
          type="text"
          value={messageInput}
          onChange={(e) => setMessageInput(e.target.value)}
          onKeyPress={handleKeyPress} // Trigger message sending on 'Enter'
          placeholder="Type a message"
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "20px",
            border: "1px solid #ccc",
            marginRight: "10px",
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            backgroundColor: "#1E3A8A",
            color: "white",
            border: "none",
            padding: "10px 20px",
            borderRadius: "20px",
            cursor: "pointer",
          }}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default Messenger;
